import React from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom";

// Components
import Login from "./components/Auth/Login";
import Register from "./components/Auth/Register";
import AdminDashboard from "./components/AdminDashboard/CreatePoll";
import ManagePolls from "./components/AdminDashboard/ManagePolls";
import ActivePolls from "./components/UserDashboard/ActivePolls";
import Vote from "./components/UserDashboard/Vote";

// Context
import { AuthProvider } from "./context/AuthContext";

// Protected Route Component
const ProtectedRoute = ({ children, role }) => {
  const user = JSON.parse(localStorage.getItem("user")); // Assume user is stored in localStorage

  if (!user) {
    return <Navigate to="/login" replace />;
  }

  if (role && user.role !== role) {
    return <Navigate to="/" replace />;
  }

  return children;
};

const App = () => {
  return (
    <AuthProvider>
      <Router>
        <Routes>
          {/* Public Routes */}
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />

          {/* Admin Routes */}
          <Route
            path="/admin/dashboard"
            element={
              <ProtectedRoute role="Admin">
                <AdminDashboard />
              </ProtectedRoute>
            }
          />
          <Route
            path="/admin/managePoll"
            element={
              <ProtectedRoute role="Admin">
                <ManagePolls />
              </ProtectedRoute>
            }
          />

          {/* User Routes */}
          <Route
            path="/user/active-polls"
            element={
              <ProtectedRoute role="User">
                <ActivePolls />
              </ProtectedRoute>
            }
          />
          <Route
            path="/user/vote/:pollId"
            element={
              <ProtectedRoute role="User">
                <Vote />
              </ProtectedRoute>
            }
          />

          {/* Default Route */}
          <Route path="*" element={<Navigate to="/login" replace />} />
        </Routes>
      </Router>
    </AuthProvider>
  );
};

export default App;
