import React, { useEffect, useState } from "react";

const ManagePolls = () => {
  const [polls, setPolls] = useState([]);

  useEffect(() => {
    const fetchPolls = async () => {
      try {
        // Simulated API response with hardcoded data
        const dummyPolls = [
          { id: 1, question: "What is your favorite color?" },
          { id: 2, question: "Do you like coffee or tea?" },
          { id: 3, question: "What is your favorite programming language?" },
        ];
        setPolls(dummyPolls);
      } catch (error) {
        console.error(error);
      }
    };
    fetchPolls();
  }, []);

  const handleDelete = async (pollId) => {
    try {
      // Simulate deleting a poll by filtering it out
      setPolls((prevPolls) => prevPolls.filter((poll) => poll.id !== pollId));
      alert("Poll deleted successfully!");
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h1>Manage Polls</h1>
      <ul>
        {polls.map((poll) => (
          <li key={poll.id}>
            <strong>{poll.question}</strong>
            <button onClick={() => handleDelete(poll.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ManagePolls;
